document.querySelector(".header__modal-button").onclick=function(){document.querySelector(".header__modal-window").style.display="block"};for(var i=0;i<document.querySelectorAll(".header__modal-window .header__item").length;i++)document.querySelectorAll(".header__modal-window .header__item")[i].onclick=function(){"block"==this.querySelector(".header__sublist").style.display?this.querySelector(".header__sublist").style.display="none":this.querySelector(".header__sublist").style.display="block"};document.querySelector(".header__modal-close").onclick=function(){document.querySelector(".header__modal-window").style.display="none"};